mapper.select:
select `type_id`,`type_value`,`type_name`,`type_desc`,`can_edit`,`create_time`,`update_time`,`create_by_id`,`update_by_id`,`create_by`,`update_by` from pz_dictionary_type

	 and type_id = #{typeId} 
	 and type_value = #{typeValue} 
	 and type_name = #{typeName} 
	 and type_desc = #{typeDesc} 
	 and can_edit = #{canEdit} 
	 and create_time = #{createTime} 
	 and update_time = #{updateTime} 
	 and create_by_id = #{createById} 
	 and update_by_id = #{updateById} 
	 and create_by = #{createBy} 
	 and update_by = #{updateBy}

mapper.delete:
delete from pz_dictionary_type where pz_dictionary_type.type_id in
#{typeId}
and pz_dictionary_type.type_id not in (select pz_dictionary.type_id from pz_dictionary where pz_dictionary.type_id = pz_dictionary_type.type_id)
